import "core-js/modules/es.array.push.js";
import axios from 'axios';
export default {
  data() {
    return {
      drawer: false,
      sidebarItems: [{
        title: 'Proyecto',
        icon: 'mdi-folder-outline'
      }, {
        title: 'Asesorias',
        icon: 'mdi-account-plus-outline'
      }],
      userId: localStorage.getItem('id'),
      project: {
        name: '',
        company: ''
      },
      file1: null,
      file2: null,
      file3: null,
      file1Uploaded: false,
      file2Uploaded: false
    };
  },
  mounted() {
    this.fetchProject();
  },
  methods: {
    navigate(index) {
      switch (index) {
        case 0:
          this.$router.push('/student');
          break;
        case 1:
          this.$router.push('/student/asesorias');
          break;
        default:
          break;
      }
    },
    logout() {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      this.$router.push('/login');
    },
    fetchProject() {
      if (!this.userId) {
        console.error('No user ID found.');
        return;
      }
      axios.get(`http://localhost:3000/users/${this.userId}`).then(response => {
        const user = response.data;
        this.file1 = user.file1 ? `${user.file1}` : null;
        this.file2 = user.file2 ? `${user.file2}` : null;
        this.file3 = user.file3 ? `${user.file3}` : null;
        if (!user.project) {
          console.error('No project ID found for the user.');
          return;
        }
        return axios.get(`http://localhost:3000/projects/${user.project}`);
      }).then(response => {
        if (response.data) {
          this.project = response.data;
        }
      }).catch(error => {
        console.error('Error fetching project data:', error);
      });
    },
    clearFile(fileNumber) {
      switch (fileNumber) {
        case 1:
          this.file1 = null;
          this.file1Uploaded = false;
          break;
        case 2:
          this.file2 = null;
          this.file2Uploaded = false;
          break;
        case 3:
          this.file3 = null;
          break;
        default:
          break;
      }
    },
    submitFiles() {
      const formData = new FormData();
      formData.append('file1', this.file1);
      axios.post(`http://localhost:3000/users/${this.userId}/files`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      }).then(response => {
        console.log('File 1 uploaded successfully:', response.data);
        this.file1Uploaded = true;
        if (this.file2) {
          this.uploadFile2();
        }
      }).catch(error => {
        console.error('Error uploading file 1:', error);
      });
    },
    uploadFile2() {
      const formData = new FormData();
      formData.append('file2', this.file2);
      axios.post(`http://localhost:3000/users/${this.userId}/files`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      }).then(response => {
        console.log('File 2 uploaded successfully:', response.data);
        this.file2Uploaded = true;
        if (this.file3) {
          this.uploadFile3();
        }
      }).catch(error => {
        console.error('Error uploading file 2:', error);
      });
    },
    uploadFile3() {
      const formData = new FormData();
      formData.append('file3', this.file3);
      axios.post(`http://localhost:3000/users/${this.userId}/files`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      }).then(response => {
        console.log('File 3 uploaded successfully:', response.data);
      }).catch(error => {
        console.error('Error uploading file 3:', error);
      });
    }
  }
};
