export default {
  props: {
    projectName: {
      type: String,
      required: true
    },
    studentCount: {
      type: Number,
      default: 0
    },
    elevation: {
      type: [Number, String],
      default: 1
    }
  },
  data() {
    return {
      isHovered: false
    };
  },
  methods: {
    onClick() {
      // Emit a click event when the card is clicked
      this.$emit('click');
    },
    onMouseEnter() {
      // Set isHovered to true when mouse enters the card
      this.isHovered = true;
    },
    onMouseLeave() {
      // Set isHovered to false when mouse leaves the card
      this.isHovered = false;
    }
  }
};
