import axios from 'axios';
import JSZip from 'jszip';
import { saveAs } from 'file-saver';
export default {
  data() {
    return {
      drawer: false,
      sidebarItems: [{
        title: 'Proyecto',
        icon: 'mdi-folder-outline'
      }, {
        title: 'Asesorias',
        icon: 'mdi-account-plus-outline'
      }],
      userName: localStorage.getItem('userName'),
      numberOfAsesoriasOptions: [],
      selectedNumberOfAsesorias: 1,
      asesorias: JSON.parse(localStorage.getItem('asesorias')) || [],
      userId: localStorage.getItem('id')
    };
  },
  computed: {
    allFilesUploaded() {
      return this.asesorias.slice(0, this.selectedNumberOfAsesorias).every(asesoria => asesoria.file || asesoria.submitted);
    }
  },
  methods: {
    navigate(index) {
      switch (index) {
        case 0:
          this.$router.push('/student');
          break;
        case 1:
          this.$router.push('/student/asesorias');
          break;
        default:
          break;
      }
    },
    logout() {
      localStorage.removeItem('token');
      this.$router.push('/login');
    },
    initializeAsesorias() {
      const initialAsesorias = Array.from({
        length: this.selectedNumberOfAsesorias
      }, (v, i) => ({
        id: i + 1,
        file: {
          data: null,
          filename: null
        },
        submitted: this.asesorias[i]?.submitted || false
      }));

      // Merge existing asesorias with initialAsesorias to retain already uploaded files
      this.asesorias = initialAsesorias.map((asesoria, i) => ({
        ...asesoria,
        file: this.asesorias[i]?.file || asesoria.file,
        submitted: this.asesorias[i]?.submitted || asesoria.submitted
      }));
    },
    async submitFiles() {
      const formData = new FormData();
      const submittedIndexes = [];
      this.asesorias.slice(0, this.selectedNumberOfAsesorias).forEach((asesoria, index) => {
        if (asesoria.file && !asesoria.submitted) {
          formData.append('files', asesoria.file);
          submittedIndexes.push(index);
        }
      });
      try {
        const response = await axios.post('http://localhost:3000/upload/multiple', formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });
        if (response.status === 201) {
          response.data.files.forEach((file, fileIndex) => {
            const index = submittedIndexes[fileIndex];
            if (index !== undefined) {
              this.asesorias[index].submitted = true;
              this.asesorias[index].file.data = file.data;
              this.asesorias[index].file.filename = file.filename;
            }
          });
          localStorage.setItem('asesorias', JSON.stringify(this.asesorias));
          window.alert('Files uploaded successfully!');
          console.log('Files uploaded successfully!');
        } else {
          console.error('Failed to upload files.');
        }
      } catch (error) {
        console.error('Error uploading files:', error);
      }
    },
    async fetchAsesorias() {
      try {
        const response = await axios.get(`http://localhost:3000/users/${this.userId}`);
        const userData = response.data;
        this.numberOfAsesoriasOptions = Array.from({
          length: userData.asesorias
        }, (v, i) => i + 1);
        this.selectedNumberOfAsesorias = userData.asesorias;
        this.initializeAsesorias();
      } catch (error) {
        console.error('Error fetching asesorías:', error);
      }
    },
    async fetchFileData() {
      try {
        const response = await axios.get('http://localhost:3000/files');
        const files = response.data;
        files.forEach((file, index) => {
          if (this.asesorias[index]) {
            this.asesorias[index].file.data = file.data;
            this.asesorias[index].file.filename = file.filename;
          }
        });
      } catch (error) {
        console.error('Error fetching file data:', error);
      }
    },
    async downloadAllFiles() {
      const zip = new JSZip();
      this.asesorias.slice(0, this.selectedNumberOfAsesorias).forEach((asesoria, index) => {
        if (asesoria.file && asesoria.file.data) {
          const byteString = atob(asesoria.file.data);
          const ab = new ArrayBuffer(byteString.length);
          const ia = new Uint8Array(ab);
          for (let i = 0; i < byteString.length; i++) {
            ia[i] = byteString.charCodeAt(i);
          }
          zip.file(`filename_${asesoria.file.id}.pdf`, ia);
        }
      });
      try {
        const content = await zip.generateAsync({
          type: 'blob'
        });
        saveAs(content, 'asesorias.zip');
      } catch (error) {
        console.error('Error generating ZIP:', error);
      }
    },
    deleteFile(index) {
      const fileId = this.asesorias[index].file.id;
      axios.delete(`http://localhost:3000/files/${fileId}`).then(response => {
        if (response.status === 200) {
          this.asesorias[index].submitted = false;
          this.asesorias[index].file = {
            data: null,
            filename: null
          };
          localStorage.setItem('asesorias', JSON.stringify(this.asesorias));
          window.alert('File deleted successfully!');
        } else {
          console.error('Failed to delete file.');
        }
      }).catch(error => {
        console.error('Error deleting file:', error);
      });
    }
  },
  mounted() {
    this.fetchAsesorias();
    this.fetchFileData();
  }
};
