import "core-js/modules/es.array.push.js";
import axios from 'axios';
export default {
  data() {
    return {
      drawer: false,
      sidebarItems: [{
        title: 'Proyecto',
        icon: 'mdi-folder-outline'
      }, {
        title: 'Asesorias',
        icon: 'mdi-account-plus-outline'
      }],
      userId: localStorage.getItem('id'),
      project: {
        name: '',
        company: ''
      },
      file1: null,
      file2: null,
      file3: null
    };
  },
  mounted() {
    this.fetchProject();
  },
  methods: {
    navigate(index) {
      switch (index) {
        case 0:
          this.$router.push('/student');
          break;
        case 1:
          this.$router.push('/student/asesorias');
          break;
        default:
          break;
      }
    },
    logout() {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      this.$router.push('/login');
    },
    fetchProject() {
      if (!this.userId) {
        console.error('No user ID found.');
        return;
      }
      axios.get(`http://localhost:3000/users/${this.userId}`).then(response => {
        const user = response.data;
        const projectId = user.project;
        if (!projectId) {
          console.error('No project ID found for the user.');
          return;
        }
        return axios.get(`http://localhost:3000/projects/${projectId}`);
      }).then(response => {
        if (response) {
          this.project = response.data;
        }
      }).catch(error => {
        console.error('Error fetching project data:', error);
      });
    },
    submitFiles() {
      const formData = new FormData();
      formData.append('file1', this.file1);
      formData.append('file2', this.file2);
      formData.append('file3', this.file3);
      axios.post(`http://localhost:3000/users/${this.userId}/files`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      }).then(response => {
        console.log('Files uploaded successfully:', response.data);
      }).catch(error => {
        console.error('Error uploading files:', error);
      });
    },
    clearFile(fieldName) {
      this[fieldName] = null;
    }
  }
};
