import "core-js/modules/es.array.push.js";
import axios from 'axios';
import ProjectCard from '@/components/ProjectCard.vue';
export default {
  components: {
    ProjectCard
  },
  data() {
    return {
      id: localStorage.getItem('id'),
      showProjects: true,
      projects: [],
      students: [],
      sidebarItems: [{
        title: 'Proyectos',
        icon: 'mdi-folder-outline'
      }],
      drawer: false,
      userName: localStorage.getItem('userName')
    };
  },
  methods: {
    getStudentsCount(projectId) {
      const a = this.students.filter(student => student.proyecto === projectId);
      return a.length;
    },
    async fetchData() {
      try {
        const teacherResponse = await axios.get(`http://localhost:3000/users/${this.id}`);
        const teacher = teacherResponse.data;
        const studentsResponse = await axios.get('http://localhost:3000/students');
        const allStudents = studentsResponse.data;
        const teacherStudents = allStudents.filter(student => teacher.alumnos.includes(student._id));
        const projectIds = teacherStudents.map(student => student.proyect);
        const projectsResponse = await axios.get('http://localhost:3000/projects');
        const projects = projectsResponse.data;
        const filteredProjects = projects.filter(project => projectIds.includes(project._id));
        this.projects = filteredProjects;
        this.students = teacherStudents;
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    },
    onClick(project) {
      // Navigate to the new route with the project ID as a route parameter
      this.$router.push({
        name: 'ProjectTable',
        params: {
          projectId: project._id
        }
      });
    },
    navigate(index) {
      this.showProjects = true;
      switch (index) {
        case 0:
          this.$router.push('/Teacher/Projects');
          break;
        case 1:
          this.$router.push('/Students');
          break;
        case 2:
          this.$router.push('/Teachers');
          break;
        default:
          break;
      }
    },
    logout() {
      localStorage.removeItem('token');
      this.$router.push('/login');
    }
  },
  mounted() {
    this.fetchData();
  }
};
