import "core-js/modules/es.array.push.js";
import axios from 'axios';
export default {
  data() {
    return {
      userName: localStorage.getItem('userName'),
      drawer: false,
      sidebarItems: [{
        title: 'Proyecto',
        icon: 'mdi-folder-outline'
      }, {
        title: 'Asesorias',
        icon: 'mdi-account-plus-outline'
      }, {
        title: 'Archivos',
        icon: 'mdi-file-document-outline'
      }],
      files: [] // Use an array to store multiple files
    };
  },
  mounted() {
    this.fetchArchivos();
  },
  computed: {},
  methods: {
    fetchArchivos() {
      axios.get('http://localhost:3000/archivos').then(response => {
        this.archivos = response.data;
      }).catch(error => {
        console.error('Error fetching archivos:', error);
      });
    },
    navigate(index) {
      switch (index) {
        case 0:
          this.$router.push('/student');
          break;
        case 1:
          this.$router.push('/student/asesorias');
          break;
        case 2:
          this.$router.push('/student/archivos');
          break;
        default:
          break;
      }
    },
    logout() {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      this.$router.push('/login');
    },
    submitForm() {
      if (!this.files || this.files.length === 0) {
        // Check if files are selected
        alert('Please choose at least one file to upload.');
        return;
      }
      const formData = new FormData();
      // Append each file to the FormData object
      for (let i = 0; i < this.files.length; i++) {
        formData.append('files', this.files[i]);
      }
      axios.post('http://localhost:3000/upload-archivo', formData).then(response => {
        console.log('Files uploaded:', response.data);
        this.fetchArchivos();
      }).catch(error => {
        console.error('Error uploading files:', error);
      });
    }
  }
};
