import "core-js/modules/es.array.push.js";
import axios from 'axios';
export default {
  data() {
    return {
      userName: localStorage.getItem('userName'),
      drawer: false,
      sidebarItems: [{
        title: 'Proyecto',
        icon: 'mdi-folder-outline'
      }, {
        title: 'Asesorias',
        icon: 'mdi-account-plus-outline'
      }, {
        title: 'Archivos',
        icon: 'mdi-file-document-outline'
      }],
      file: null
    };
  },
  mounted() {
    this.fetchArchivos();
  },
  computed: {},
  methods: {
    fetchArchivos() {
      axios.get('/archivos').then(response => {
        this.archivos = response.data;
      }).catch(error => {
        console.error('Error fetching archivos:', error);
      });
    },
    navigate(index) {
      switch (index) {
        case 0:
          this.$router.push('/student');
          break;
        case 1:
          this.$router.push('/student/asesorias');
          break;
        case 2:
          this.$router.push('/student/archivos');
          break;
        default:
          break;
      }
    },
    logout() {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      this.$router.push('/login');
    },
    submitForm() {
      if (!this.file) {
        alert('Please choose a file to upload.');
        return;
      }
      const formData = new FormData();
      formData.append('file', this.file);
      axios.post('/upload-archivo', formData).then(response => {
        // File uploaded successfully, you can handle the response as needed
        console.log('File uploaded:', response.data);
        // Optionally, you can fetch the updated list of archivos
        this.fetchArchivos();
      }).catch(error => {
        console.error('Error uploading file:', error);
      });
    }
  }
};
