import "core-js/modules/es.array.push.js";
import axios from 'axios';
export default {
  data: () => ({
    alert: false,
    valid: true,
    email: '',
    emailRules: [v => !!v || 'Correo requerido', v => /.+@.+\..+/.test(v) || 'Correo debe ser válido'],
    password: '',
    passwordRules: [v => !!v || 'Contraseña requerida']
  }),
  methods: {
    async submit() {
      try {
        const response = await axios.post('http://localhost:3000/login', {
          correo: this.email,
          contraseña: this.password
        });
        if (response.status === 200) {
          const {
            token,
            rol,
            nombre,
            id
          } = response.data;

          // Store the token and the user's name in localStorage
          localStorage.setItem('token', token);
          localStorage.setItem('userName', nombre);
          localStorage.setItem('id', id);
          switch (rol) {
            case 'admin':
              this.$router.push('/Admin');
              break;
            case 'student':
              this.$router.push('/Student');
              break;
            case 'teacher':
              this.$router.push('/Teacher/projects');
              break;
            default:
              // Redirect to a default route for unknown roles
              this.$router.push('/default-route');
              break;
          }
          console.log('Login successful:', response.data);
        } else {
          // Handle other status codes if needed
        }
      } catch (error) {
        console.error('Login error:', error.response.data.error);
        this.alert = true;
      }
    }
  }
};
