import "core-js/modules/es.array.push.js";
import axios from 'axios';
export default {
  data() {
    return {
      drawer: false,
      sidebarItems: [{
        title: 'Proyecto',
        icon: 'mdi-folder-outline'
      }, {
        title: 'Asesorias',
        icon: 'mdi-account-plus-outline'
      }],
      userName: localStorage.getItem('userName'),
      // Retrieve the user's name from localStorage
      numberOfAsesorias: 1,
      // Default to 1 asesoria
      asesorias: [{
        id: 1,
        file: null,
        submitted: false
      }],
      // Initialize with one asesoria
      userId: localStorage.getItem('id')
    };
  },
  computed: {
    allFilesSubmitted() {
      return this.asesorias.every(asesoria => asesoria.submitted);
    }
  },
  methods: {
    navigate(index) {
      switch (index) {
        case 0:
          this.$router.push('/student');
          break;
        case 1:
          this.$router.push('/student/asesorias');
          break;
        default:
          break;
      }
    },
    logout() {
      localStorage.removeItem('token');
      this.$router.push('/login');
    },
    initializeAsesorias() {
      this.asesorias = Array.from({
        length: this.numberOfAsesorias
      }, (v, i) => ({
        id: i + 1,
        file: null,
        submitted: false
      }));
    },
    async submitFiles() {
      const formData = new FormData();
      this.asesorias.forEach((asesoria, index) => {
        if (asesoria.file && !asesoria.submitted) {
          formData.append(`files[${index}]`, asesoria.file);
          console.log(asesoria.file);
        }
      });
      try {
        const response = await axios.post('http://localhost:3000/upload', formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });
        if (response.status === 201) {
          response.data.files.forEach((file, index) => {
            this.asesorias[index].submitted = true;
            this.$set(this.asesorias, index, this.asesorias[index]);
          });
          console.log('Files uploaded successfully!');
        } else {
          console.error('Failed to upload files.');
        }
      } catch (error) {
        console.error('Error uploading files:', error);
      }
    },
    async fetchAsesorias() {
      try {
        const response = await axios.get(`http://localhost:3000/users/${this.userId}`);
        const userData = response.data;
        this.numberOfAsesorias = userData.asesorias; // Set numberOfAsesorias to the value fetched from the backend
        this.asesorias = []; // Clear the asesorías array
        for (let i = 0; i < this.numberOfAsesorias; i++) {
          this.asesorias.push({
            id: i + 1,
            file: null,
            submitted: false
          }); // Initialize the asesorías array based on the fetched value
        }
      } catch (error) {
        console.error('Error fetching asesorías:', error);
      }
    }
  },
  mounted() {
    this.fetchAsesorias();
  }
};
